import { Component, OnInit } from '@angular/core';
import { take } from 'rxjs';
import { UserService } from 'src/app/service/user.service';
import * as moment from 'moment';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent implements OnInit{

  user: any;
  dataIscrizione: any;

  constructor(private userService: UserService,){}

  ngOnInit(): void {
    if(JSON.parse(localStorage.getItem('user'))){
      this.user = JSON.parse(localStorage.getItem('user'));
      this.dataIscrizione = moment(this.user.createdAt).locale('it').format('dddd DD MMMM YYYY');
    }
  }



}
<div class="container-main">
    <div class="container-profilo">
        <div class="container-immagine">
        </div>
        <div class="dati-utente">
            <h2>Profilo Utente:</h2>
            <ul class="column1">
                <li>
                    <span>Nome: </span>{{user.name}}
                </li>
                <li>
                   <span>Cognome: </span>{{user.lastname}}
                </li>
                <li>
                    <span>Email: </span>{{user.email}}
                </li>
            </ul>
            <ul>
                <li>
                    <span>Roulo: </span>{{user.role}}
                </li>
                <li>
                    <span>Data di iscrizione: </span>{{dataIscrizione}}
                </li>
            </ul>

        </div>

    </div>


