import { Component, Input } from '@angular/core';
import { Observable, map } from 'rxjs';
import { Mostra } from 'src/app/models/mostra.model';
import { MostraService } from 'src/app/service/mostra.service';


@Component({
  selector: 'app-mostra-card',
  templateUrl: './mostra-card.component.html',
  styleUrls: ['./mostra-card.component.scss']
})
export class MostraCardComponent {

  mostre: Mostra[];
  page = 1;

  mostrePerPagina = 6;

  constructor(private mostraService: MostraService){}

  mostre$: Observable<Mostra[]> = this.mostraService.getMostre().pipe(
    map(res => this.mostre = res),
  );

  paginate(event){
    event.page = event.page + 1;
    this.page = event.page;
   }

}
<div class="container-cards">
  <div class="card-container" *ngFor="let mostra of mostre$ | async | slice: (page * mostrePerPagina) - mostrePerPagina : page * mostrePerPagina">
    <div class="card">
        <div class="card-image" [ngStyle]="{'background-image': 'url(' + mostra.image +')'}"></div>
        <div class="card-body">
          <h5 class="card-title">{{mostra.title}}</h5>
          <p class="card-text">{{mostra.description | slice:0:250}}</p>
          <div class="card-button">
            <a routerLink="/mostre/dettaglio/{{mostra.title}}/{{mostra._id}}" class="btn btn-primary">visualizza</a>
          </div> 
        </div>
      </div>
  </div>
  <div>
    <div class="container-paginator">
      <div>
        <strong>{{mostrePerPagina}}</strong> Mostre su un totale di <strong>{{mostre.length}}</strong> mostre, pagina corrente: <strong>{{page}}</strong>
      </div>
      <p-paginator (onPageChange)="paginate($event)" [rows]="mostrePerPagina" [totalRecords]="mostre.length" (rowsPerPageOptionsChange)="mostrePerPagina"></p-paginator>
    </div>

  </div>
</div>
