import { MostraService } from './../../../service/mostra.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Mostra } from './../../../models/mostra.model';

@Component({
  selector: 'app-detail',
  templateUrl: './detail.component.html',
  styleUrls: ['./detail.component.scss']
})
export class DetailComponent implements OnInit{

  mostra: Mostra;


  constructor(
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private MostraService: MostraService
  ){}

  onGetMostra(): void{
    const id = this.activatedRoute.snapshot.paramMap.get('_id');

    this.MostraService.getMostra(id).subscribe({
      next: (res)=> {
        this.mostra = res;
      },
      error: (err) => {
        console.log(err);
      }
    })
  }

  ngOnInit(): void{
    this.onGetMostra();
  }

}
<div class="container-fluid">
    <div class="colonna-sx" [ngStyle]="{'background-image':'url('+ mostra.image + ')'}">
    </div>
    <div class="colonna-dx">
        <h2>{{mostra.title}}</h2>
        <p>{{mostra.description}}</p>
        <h4><strong>Luogo: </strong>{{mostra.place}}</h4>
        <h4><strong>Data ultima per vedere la mostra: </strong>{{mostra.endDate}}</h4>
    </div>
</div>
